import React from 'react';
import { useEffect } from 'react/cjs/react.development';

const MovieList = ({favoriteComponent, movies, handleFavoritesClick}) => {
    const FavoriteComponent = favoriteComponent;
    const [localMovies, setLocalMovies] = useState([]);

    //useEffect(() => {}) //pegar a lista de ids de filmes e requisitar o resto das informações para serem utilizadas

    useEffect(()=> {
        movies.map((movie)=> {
            const url = `https://www.omdbapi.com/?i=tt0082971&apikey=201f1cda` //template string
            fetch(url).then(async (response) => {
                setLocalMovies(await response.json());
                }).catch(console.log)
            }
        )
    });

    return (
        <>
            {localMovies.map((movie, index)=> (
                <div className='image-container d-flex justify-content-start m-3'>
                    <div className ='name-container d-flex align-items-center justify-content-center'><p>{movie.Title} <br></br> {movie.Year}</p></div>
                    <img src={movie.Poster} alt="movie"></img>
                    <div onClick ={()=>handleFavoritesClick(movie)}
                    className ='overlay d-flex align-items-center justify-content-center'>

                    <FavoriteComponent/>


                    </div>
                </div>
            ))}
        </>
    );
};

export default MovieList;
