import React, { useEffect, useState } from "react";
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Search from "./pages/Search";
import Library from "./pages/Library";
import { Routes, Route, Link } from "react-router-dom";
import Toastr from "./components/Toastr";
import '../App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import MovieList from "./components/MovieList";
import MovieListHeading from "./components/MovieListHeading";
import SearchBox from "./components/SearchBox";
import AddFavorites from "./components/AddFavorites";
import RemoveFavorites from "./components/RemoveFavorites";
import PlayReview from "./components/PlayReview"
import FavoritesMovieList from "./components/FavoritesMovieList";
import { toast } from "react-toastify";
import AddReview from "./components/AddReview";

const App = () => {
  const [favorites, setFavorites] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');


  const getMovieRequest = async (searchValue) => {
    const url = `https://www.omdbapi.com/?s=${searchValue}&apikey=201f1cda` //template string

    const response = await fetch(url);
    const responseJson = await response.json(); //converts http response into json

    if(responseJson.Search){ //search only if its a value
      setMovies(responseJson.Search);
    }

  };

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  useEffect(()=> {
    const url = `http://localhost:3000/api/moovy` //template string

    fetch(url).then(async (response) => {
      setFavorites(await response.json());
    }).catch(console.log)

  }, [favorites]); //just on page load

  const addFavoriteMovie = (movie) => {
    fetch("http://localhost:3000/api/moovy", {
      // Adding method type
      method: "POST",

      // Adding body or contents to send
      body: JSON.stringify({
          movieID: movie.imdbID,
          isReview: 0
      }),

      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
    })

  // Displaying popups according to the response status
    .then(json => {
      if(json.status === 201){
        toast.success('Added to your Library');
      }else{
        toast.error('The movie is already on your Library');
      }
    });
  };

  useEffect(()=> {
    const url = `http://localhost:3000/api/moovy` //template string

    fetch(url).then(async (response) => {
      setFavorites(await response.json());
    }).catch(console.log)

  }, [favorites]); //just on page load

  const removeFavoriteMovies = (movie) => {
    fetch(`http://localhost:3000/api/moovy/${movie.imdbID}`, {
    // Adding method type
    method: "DELETE",
    })
    // Displaying results to console and popup confirmation
    .then(json => {
      console.log(json);
      if(json.status === 200){
        toast.success('Removed from your Library');
      }
    });
  };

  useEffect(()=> {
    const url = `http://localhost:3000/api/reviews` //template string

    fetch(url).then(async (response) => {
      setReviews(await response.json());
    }).catch(console.log)

  }, [reviews]); //just on page load

  const removeReview = (movie) => {
    fetch(`http://localhost:3000/api/reviews/${movie.imdbID}`, {
    // Adding method type
    method: "DELETE",
    })
    // Displaying results to console and popup confirmation
    .then(json => {
      console.log(json);
      if(json.status === 200){
        toast.success('Review was removed.');
      }
    });
  };

  return  (
  <div>
    <nav className="navbar navbar-expand-lg navbar-light bg-light">

      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
          <li className="nav-item active">
            <a className="nav-link navbar-brand">Moovy</a>
          </li>
          <li className="nav-item active">
            <span className="sr-only nav-link"><Link to="/search" className="nav-components">Search</Link></span>
          </li>
          <li className="nav-item">
            <span className="sr-only nav-link"><Link to="/" className="nav-components">Library</Link></span>
          </li>
        </ul>
      </div>
    </nav>

    <Routes>
        <Route path="/" element={<Library/>} />
        <Route path="search" element={<Search/>} />
    </Routes>
  </div>
  );
};

export default App;
