import React, { useState } from 'react';
import { useEffect } from 'react/cjs/react.development';

const MovieList = ({favoriteComponent, movies, handleFavoritesClick}) => {
    const FavoriteComponent = favoriteComponent;
    const [localMovies, setLocalMovies] = useState([]);

    useEffect(() => {
        const promises = movies.map(async (movie)=> {
            const url = `https://www.omdbapi.com/?i=${movie.imdbID}&apikey=201f1cda` //template string 

            return (await fetch(url)).json();
            }
        )
        Promise.all(promises).then(setLocalMovies);
      }, [movies]);

    return (
        <>
            {localMovies.map((movie, index)=> (
                <div className='image-container d-flex justify-content-start m-3' key={movie.imdbID}>
                    <div className ='name-container d-flex align-items-center justify-content-center'><p>{movie.Title} <br> <svg height="25" width="23" fill="#ffd055" data-rating="2">
    <polygon points="9.9, 1.1, 3.3, 21.78, 19.8, 8.58, 0, 8.58, 16.5, 21.78" style="fill-rule:nonzero;"/>
  </svg></br> {movie.imdbRating}</p></div>
                    <img src={movie.Poster} alt="movie"></img>
                    <div onClick ={()=>handleFavoritesClick(movie)}
                    className ='overlay d-flex align-items-center justify-content-center'>

                    <FavoriteComponent/>
                    </div>
                </div>
            ))}
        </>
    );
};

export default MovieList;
