import React, { useState } from 'react';
import { useEffect } from 'react/cjs/react.development';

const MovieList = ({favoriteComponent, movies, handleFavoritesClick}) => {
    const FavoriteComponent = favoriteComponent;
    const [localMovies, setLocalMovies] = useState([]);

    useEffect(() => {
        const promises = movies.map(async (movie)=> {
            const url = `https://www.omdbapi.com/?i=${movie.imdbID}&apikey=201f1cda` //template string 

            return (await fetch(url)).json();
            }
        )
        Promise.all(promises).then(setLocalMovies);
      }, [movies]);

    return (
        <>
            {localMovies.map((movie, index)=> (
                <div className='image-container d-flex justify-content-start m-3' key={movie.imdbID}>
                    <div className ='name-container d-flex align-items-center justify-content-center'><p>{movie.Title} <br></br> <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-star" viewBox="0 0 16 16">
  <path d="M2.866 14.85c-.078.444.36.791.746.593l4.39-2.256 4.389 2.256c.386.198.824-.149.746-.592l-.83-4.73 3.522-3.356c.33-.314.16-.888-.282-.95l-4.898-.696L8.465.792a.513.513 0 0 0-.927 0L5.354 5.12l-4.898.696c-.441.062-.612.636-.283.95l3.523 3.356-.83 4.73zm4.905-2.767-3.686 1.894.694-3.957a.565.565 0 0 0-.163-.505L1.71 6.745l4.052-.576a.525.525 0 0 0 .393-.288L8 2.223l1.847 3.658a.525.525 0 0 0 .393.288l4.052.575-2.906 2.77a.565.565 0 0 0-.163.506l.694 3.957-3.686-1.894a.503.503 0 0 0-.461 0z"/>
</svg>{movie.imdbRating}</p></div>
                    <img src={movie.Poster} alt="movie"></img>
                    <div onClick ={()=>handleFavoritesClick(movie)}
                    className ='overlay d-flex align-items-center justify-content-center'>

                    <FavoriteComponent/>
                    </div>
                </div>
            ))}
        </>
    );
};

export default MovieList;
