import React, { useEffect, useState } from "react";
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Search from "./pages/Search";
import Library from "./pages/Library";
import { Routes, Route, Link } from "react-router-dom";

const App = () => {
  return  (
  <div classNameName ='container-fluid movie-app'>
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <a className="navbar-brand">Moovy</a>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>

      <div classNameName="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
          <li className="nav-item active">
            <a className="nav-link"><span className="sr-only"><Link to="/search" style={{paddingLeft: 13, textDecoration: 'none'}}>Search</Link></span></a>
          </li>
          <li className="nav-item">
            <a className="nav-link"><span className="sr-only"><Link to="/">Library</Link></span></a>
          </li>
        </ul>
      </div>
    </nav>
    <Routes>
        <Route path="/" element={<Library/>} />
        <Route path="search" element={<Search/>} />
    </Routes>
  </div>
  );
};

export default App;
