import React, { useEffect, useState } from "react";
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import MovieList from "./components/MovieList";
import MovieListHeading from "./components/MovieListHeading";
import SearchBox from "./components/SearchBox";
import AddFavorites from "./components/AddFavorites";
import RemoveFavorites from "./components/RemoveFavorites";

const App = () => {
  const [movies, setmovies] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const url = `https://www.omdbapi.com/?s=${searchValue}&apikey=201f1cda` //template string

    const response = await fetch(url);
    const responseJson = await response.json(); //converts http response into json

    if(responseJson.Search){ //search only if its a value
      setmovies(responseJson.Search);
    }

  };

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  useEffect(()=> {
    const movieFavorites = JSON.parse(
      localStorage.getItem('react-movie-app-favorites') //get list of favorites from local storage
      );

      setFavorites(movieFavorites); //display list of favorites
  }, []); //just on page load

  const saveToLocalStorage = (items) => {
    localStorage.setItem('react-movie-app-favorites', JSON.stringify(items))
  }

  const addFavoriteMovie = (movie) => {
    const newFavoriteList = [...favorites, movie];
    setFavorites(newFavoriteList);
    saveToLocalStorage(newFavoriteList); //save favorites locally
  }

  const removeFavoriteMovie = (movie) => {
    const newFavoriteList = favorites.filter(
      (favorite) => favorite.imdbID !== movie.imdbID
      );
      setFavorites(newFavoriteList);
  };

  return  (
  <div className ='container-fluid movie-app'>
    <div className = 'row d-flex align-items-center mt-4 mb-4'>
      <MovieListHeading heading = 'Moovy'/>
      <SearchBox searchValue={searchValue} setSearchValue={setSearchValue}/>
    </div>
    <div className='d-flex'>
      <MovieList 
      movies = {movies} 
      handleFavoritesClick = {addFavoriteMovie}
      favoriteComponent = {AddFavorites}/>
    </div>
    <div className = 'row d-flex align-items-center mt-4 mb-4'>
      <MovieListHeading heading = 'My Library'/>
    </div>
    <div className='row d-flex'>
      <MovieList 
      movies = {favorites} 
      handleFavoritesClick = {removeFavoriteMovie}
      favoriteComponent = {RemoveFavorites}/>
    </div>
  </div>
  );
};

export default App;
