          playing: false
        }
        this.handlePlay = this.handlePlay.bind(this)
        this.handlePause = this.handlePause.bind(this)
      }

      handlePlay () {
        if(this.playing === true){
            this.setState({
                playing: false
            })
        }else{
            this.setState({
                playing: true
