import React, { useEffect, useState } from "react";
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import MovieList from "./components/MovieList";

const App = () => {
  const [movies, setmovies] = useState([
    {
        "Title": "Joker",
        "Year": "2019",
        "imdbID": "tt7286456",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNGVjNWI4ZGUtNzE0MS00YTJmLWE0ZDctN2ZiYTk2YmI3NTYyXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_SX300.jpg"
    },
    {
        "Title": "Batman Beyond: Return of the Joker",
        "Year": "2000",
        "imdbID": "tt0233298",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BNmRmODEwNzctYzU1MS00ZDQ1LWI2NWMtZWFkNTQwNDg1ZDFiXkEyXkFqcGdeQXVyNTI4MjkwNjA@._V1_SX300.jpg"
    },
    {
        "Title": "Joker",
        "Year": "2012",
        "imdbID": "tt1918886",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BMjE0NjIxODMxN15BMl5BanBnXkFtZTcwMjM5MDcxOA@@._V1_SX300.jpg"
    },
  ]);

  const getMovieRequest = async () => {
    const url = "https://www.omdbapi.com/?s=joker&apikey=201f1cda"

    const response = await fetch(url);
    const responseJson = await response.json(); //converts http response into json

    console.log(responseJson);
    setmovies(responseJson.Search);
  };

  useEffect(() => {
    getMovieRequest();
  }, []);

  return  (
  <div className ='container-fluid movie-app'>
    <div className='d-flex flex-md-row flex-column justify-content-between'>
      <MovieList movies = {movies}/>
    </div>
  </div>
  );
};

export default App;
